        int n=a.size();
        int ii,ji;
        for(int i=n-1;i>0;i--){
            if(a[i]>a[i-1]){
                ii=i;
               break;
            }
        }
        if(ii==0){
            sort(a.begin(),a.end());
        }
        else{
            int min=INT_MAX;
            int ts=a[ii-1];
            for(int j=n-1;j>ii-1;j--){
                if(a[j]>ts && a[j]-ts<min ){
                    int temp=a[j];
                    a[j]=ts;
                    a[ii-1]=temp;
                    min=a[j]-ts;
                }
            }
            sort(a.begin()+ii,a.end());
        }



    }


};

