**Code May look longer , I intentionally made it longer**  😎
```
class Solution
{
private:
    vector<vector<string>> res;  
	// <- This will contain all the generated vectors , from this we'll erase the wrong ones

public:
	//This will check if the string is palindrome or not
    bool checkPalindrome(string &s)
    {
        int l = 0, r = s.size() - 1;
        while (l <= r)
            if (s[l] != s[r])
                return false;
            else
                l++, r--;
        return true;
    }



	/*
	This will check a vector's all content ,
	if any string of this vector is not palindrome whole vector will be removed
	*/
    bool checkVector(vector<string> &v)
    {


        for (auto &s : v)
            if (!checkPalindrome(s))   // just checking all the strings 
                return true;  	//return true : Means this vector will be removed
        return false; 	//return false : Means this vector will NOT be removed
    }
    void recur(string &s)
    {
        if (s.size() == 1)
        {
            res.push_back({s});
            return;
        }
        string c = "";
        c += s.back();
        s.pop_back();
        recur(s);      //To get the answer for n-length string we need answer of (n-1)th string
        int n = res.size();
        for (int i = 0; i < n; i++)
        {
            res.push_back(res[i]);
            res.back().back() += c;      // This is the 2nd option {'a' +'b + c'}
            res[i].push_back(c);       // 1st one  {'a' + 'b' } +'c'
        }
    }

    vector<vector<string>> partition(string s)
    {
        recur(s);
		/*
		remove_if is from STL.
		It removes elements from specified range and a specified conditions.
		For us range = whole vector = from begin() to end()
		ans condition = checkVector() <- Explained above
        */
		auto it = remove_if(res.begin(), res.end(), [&](vector<string> &v)     
                            { return checkVector(v); });
        res.resize(distance(res.begin(), it));  // resizing it bcz after removing elements size will be decreased
        return res;
    }
};
```

