class Solution {
public:
    vector<vector<string>> partition( string s ) {
        int n = s.size();
        vector<vector<bool>> dp( n, vector<bool>(n,false) );
    	for( int i=0; i<n; i++ ) dp[i][i] = true;

        for( int i=n-1; i >= 0; i-- )
    		for( int j=i+1; j < n; j++ )
		    	if( s[i] == s[j] && ( dp[i+1][j-1] || j-i <= 2 ) )
			    	dp[i][j] = true;

        vector<vector<string>> result;
        vector<string> curr;
        backtrack( result, curr, 0, s, dp );
        return result;
    }

private:
    void backtrack( vector<vector<string>>& result, vector<string>& curr,
                   int start, string& s, vector<vector<bool>> &dp ) {
        if( start == s.length() ) {
            result.push_back( curr );
            return;
        }
        for( int i=start; i < s.size(); i++ ) {
            if( dp[start][i] ) {
                curr.push_back( s.substr( start, i - start + 1 ) );
                backtrack( result, curr, i+1, s, dp );
                curr.pop_back();
            }
        }
    }
};
