class Solution {
public:
    bool checkPalin(string s){
        string s1 = s;
        reverse(s.begin(), s.end());
        return s==s1;
    }

    void helper(string s, vector<string> &localResult, vector<vector<string>> &result){
        if(s.length() == 0){
            result.push_back(localResult);
            return;
        }

        for(int i = 0; i<s.length(); i++){
            if(checkPalin(s.substr(0,i+1))){
                localResult.push_back(s.substr(0, i+1));
                helper(s.substr(i+1), localResult, result);
                localResult.pop_back();
            }
        }
        return;

    }

    vector<vector<string>> partition(string s) {
        vector<vector<string>> result;
	    vector<string> localResult;
	    helper(s, localResult, result);
	    return result;
    }
};
