#define vvb vector<vector<bool>>

class Solution {
public:

    vector<vector<string>> finalAns;
    vector<string> ans;
    int n;

    // O(2^N)
    void findPalindromes(string &s, int st, vvb &isPalin) {

        if(st == n) {
            finalAns.push_back(ans);
            return;
        }

        string currString = "";

        for(int en = st ; en < n ; en++) { 

            if( st == en or (s[st] == s[en] and (st+1==en or isPalin[st+1][en-1])) ) {
                isPalin[st][en] = true;
            }

            currString += s[en]; // substr not used

            if(isPalin[st][en]) {
                ans.push_back(currString);
                findPalindromes(s,en+1,isPalin); 
                ans.pop_back();
            } 
        }
    }

    vector<vector<string>> partition(string s) {
        n = s.size();    
        vector<vector<bool>> isPalin(n, vector<bool>(n,false));
        findPalindromes(s,0,isPalin);
        return finalAns;
    }
};
