    void helper(vector<vector<string>> &pResult, string &pString, vector<string> &pTemp, int pIndex) {

        if (pIndex >= pString.size()) {
            pResult.push_back(pTemp);
            return;
        }

        for (int aLen=1;pIndex+aLen<=pString.size();aLen++) {
            string aChunk = pString.substr(pIndex, aLen);
            if (isPally(aChunk)) {
                pTemp.push_back(aChunk);
                helper(pResult, pString, pTemp, pIndex + aLen);
                pTemp.pop_back();
            }   
