class Solution {
private:
    int countLength(vector<string> v) {
        int length = 0;
        for(int i = 0; i < v.size(); i++) {
            length += v[i].size();
        }
        return length;
    }
    bool isPalindrome(string s) {
        int n = s.size();
        if(n == 1) return true;
        int i = 0, j = n-1;
        while(i < j) {
            if(s[i] != s[j])
                return false;
            i++;
            j--;
        }
        return true;
    }
    void backTrack(vector<vector<string>>& res, vector<string>& curr, string& s, int start) {
        if(countLength(curr) == s.size())
            res.push_back(curr);

        for(int i = start; i < s.size(); i++) {
            string temp = s.substr(start, i-start+1);
            if(!isPalindrome(temp)) continue;
            curr.push_back(temp);
            backTrack(res, curr, s, i+1);
            curr.pop_back();
        }
    }

public:
    vector<vector<string>> partition(string s) {
        vector<vector<string>> res;
        vector<string> curr;
        backTrack(res, curr, s, 0);
        return res;
    }
};
