                currParts.add(s.substring(idx, i + 1));
                help(s, i + 1, currParts);
                currParts.remove(currParts.size() - 1);
            }
        }
    }

    private boolean isPalindrome(String s, int l, int r) {
        while (l < r) {
            if (s.charAt(l++) != s.charAt(r--)) {
                return false;
            }
        }

        return true;
    }
}
