class Solution {
public:
    vector<vector<vector<string>>>partitionspossibletillhere;
    vector<bool>zeros;
    vector<vector<bool>>ispallindrome;
    string str;
    void assign(string &s)
    {
        zeros.assign(s.size(),false);
        ispallindrome.assign(s.size(),zeros);
        partitionspossibletillhere.assign(s.size(),{});
        int i,j;
        ispallindrome[0][0]=true;
        for(i=1;i<s.size();i++)
        {
            ispallindrome[i][i]=true;
            if(s[i]==s[i-1])
            {
                ispallindrome[i-1][i]=true;
            }
        }
        for(j=2;j<=s.size()-1;j++)
        {
            for(i=j;i<s.size();i++)
            {
                if((s[i]==s[i-j])&&(ispallindrome[i-j+1][i-1]==1))
                {
                    ispallindrome[i-j][i]=true;
                }
            }
        }
        str=s;
        return;
    }
    void partitioning(int index)
    {
        if(index==str.size())
        {
            return;
        }
        if(ispallindrome[0][index])
        {
            partitionspossibletillhere[index].push_back({str.substr(0,index+1)});
        }
        for(int i=0;i<index;i++)
        {
            if(ispallindrome[i+1][index])
            {
                string topush=str.substr(i+1,index-i);
                for(auto j:partitionspossibletillhere[i])
                {
                    j.push_back(topush);
                    partitionspossibletillhere[index].push_back(j);
                }
            }
        }
        partitioning(index+1);
        return;
    }
    vector<vector<string>> partition(string s) 
    {
        assign(s);
        partitioning(0);
        return partitionspossibletillhere.back();
    }
};
