class Solution:
    def __init__(self):
        self.isPalindrome = None

    def helper(self, s: str, index: int, path: List[str], results: List[List[str]]):
        if index == len(s):
            results.append(path)
            return

        for end in range(index, len(s)):
            if self.isPalindrome[index][end]:
                newPath = path[:]
                newPath.append(s[index:end+1])
                self.helper(s, end + 1, newPath, results)

    def partition(self, s: str) -> List[List[str]]:
        self.isPalindrome = [[False for i in range(len(s))] for j in range(len(s))]

        for i in range(len(s)):
            self.isPalindrome[i][i] = True

        for length in range(2, len(s) + 1):
            for start in range(len(s) - length + 1):
                end = start + length - 1
                if s[start] == s[end]:
                    if length == 2:
                        self.isPalindrome[start][end] = True
                    else:
                        self.isPalindrome[start][end] |= self.isPalindrome[start + 1][end - 1]

        results = []
        self.helper(s, 0, [], results)
        return results

