class Solution {
public:
// define matrix to check palindrome from i to j
    vector<vector<bool>> dp;

	// answer vector
    vector<vector<string>> ans;

	//palindrome function return true if s is palindrome else returns false
    bool ispalin(string s){
        int n=s.size();
        for(int i=0;i<=n/2;i++){
            if(s[i]!=s[n-1-i]) return false;
        }
        return true;
    }

	// temporary answer for each choice
    vector<string> v;

	//recursive function to implement backtracking
    void solve(string s,int i,int j,int n){

	// if i reaches to maximum length that is n then push temporary answer to final answer
        if(i==n){
            ans.push_back(v);
            return;
        }
        for(;j<n;j++){
            if(dp[i][j]){
                v.push_back(s.substr(i,j-i+1));
                solve(s,j+1,j+1,n);
                v.pop_back();
            }
        }

        return;

    }

    vector<vector<string>> partition(string s) {
        int n=s.size();
        dp.resize(n+1,vector<bool>(n+1,0));

        // initializing and filling all entries of matrix
        for(int i=0;i<n;i++){
            for(int j=i;j<n;j++){
                if(i==j) dp[i][j]=1;
                else if(ispalin(s.substr(i,j-i+1))){
                    dp[i][j]=1;
                }
            }
        }
        solve(s,0,0,n);
        return ans;

    }
};
```
