1 <= target <= 30


Solution:
```
class Solution {
public:
    void helper(vector<vector<int>> &out,vector<int> temp,vector<int> A,int remain,int index)
    {
        if(remain<0)return;
        else if(remain==0)out.push_back(temp);
        else
        {
            for(int i=index;i<A.size();i++)
            {
                if(i>index&&A[i]==A[i-1])continue;
                temp.push_back(A[i]);
                helper(out,temp,A,remain-A[i],i+1);
