    def partition(self, s: str) -> List[List[str]]:
        def recur(s,part,res):
            if not s:
                res.append(part)
            n=len(s)
            for i in range(1,n+1):
                if s[:i]==s[:i][::-1]:
                    recur(s[i:],part+[s[:i]],res)
        res=[]
        recur(s,[],res)
        return res
