class Solution:
    def partition(self, s: str) -> List[List[str]]:
        def isPalindrome(s):
            left = 0
            right = len(s) - 1
            while(left<=right):
                if s[left] != s[right]:
                    return False
                left += 1
                right -= 1
            return True

        def helper(palindromes, start, end):
            nonlocal result
			# will only happen when the string is divided correctly into palindromes
            if start >= len(s): 
                result.append(palindromes)
                return
			# end overflow, so return
            if end >=len(s):
                return
			# check if the values of start and end make a palindrome
            if isPalindrome(s[start:end+1]):
                helper(palindromes+[s[start:end+1]], end+1, end+1)
            # keep going with the same value of start and increment end by 1
            helper(palindromes, start, end+1)

        result = []
        helper([], 0, 0)
        return result

