func partition(s string) [][]string {
    res := [][]string{}
    helper(s, 0, []string{}, &res)
    return res
}

func helper(s string, index int, S []string, res *[][]string) {
    if index == len(s) {
        *res = append(*res, append([]string{}, S...))
        return 
    }
    for i := index + 1; i <= len(s); i++ {
        if isPalindrome(s[index:i]) {
            S = append(S, s[index:i])
            helper(s, i, S, res)
            S = S[:len(S)-1]
        }
    }
}

