class Solution {
    public List<List<String>> partition(String s) {
        List<List<String>> partitions = new ArrayList<>();
        if (s == null || s.length() == 0) 
			return partitions;
        addPalindromePartitions(partitions, new ArrayList<>(), s);
        return partitions;
    }

    private void addPalindromePartitions(List<List<String>> partitions, List<String> partition, String s) {
	    //base case -- we recurse until the substring given is empty string
	    //if it means its empty string it implies we cannot backtrack anymore so add the current list to the solution
        if (s.length() == 0) {
            partitions.add(new ArrayList<>(partition));
            return;
        }

        for (int i = 1; i <= s.length(); i++) {
		    //check if the current prefix from 0 to i is a palindrome. If it happens to be a palindrome, add the
			//current prefix to the recursion list, and recurse further with the suffix as a substring
			//discard the current add once we are done with the current backtrack call
            if (isPalindrome(s, i - 1)) {
                partition.add(s.substring(0, i));
                addPalindromePartitions(partitions, partition, s.substring(i));
                partition.remove(partition.size() - 1);
            }
        }
    }

    private boolean isPalindrome(String s, int right) {
        int left = 0;
        while (left < right) {
            if (s.charAt(left++) != s.charAt(right--)) 
				return false;
        }
        return true;
    }
}
