class Solution(object):
    def partition(self, s, set_pal=set(), memo={}):
        if not len(s):
            return []
        if s in memo:
            return memo[s]
        ret_list, flag = [], False
        for i in range(1, len(s) + 1):
            left = s[:i]
            if left in set_pal or left == left[::-1]:
                set_pal.add(left)
                list_rec = self.partition(s[i:], set_pal, memo)
                if list_rec is not None:
                    flag = True
                    for this_list in list_rec:
                        ret_list.append([left] + this_list)
                    if not list_rec:
                        ret_list.append([left])

        memo[s] = ret_list if flag else None
        return memo[s]

class Solution {
public:

  vector<vector<string>> partition(string s) {
    int n = s.size();
    vector<vector<bool>> dp(n, vector<bool>(n, false));
    //   build dp : if i through j (inclusive) makes a palindrome, dp[i][j] = true;
    for(int l = 1; l <=n; l++){
      for(int i = 0; i < n; i++){
        int j = i + l - 1;
        if(j >= n) break;
        dp[i][j] = (i + 1 > j - 1 || dp[i + 1][j - 1]) && s[i] == s[j];
      }
    }

    //  dfs
    vector<string> path;
    vector<vector<string>> result;
    dfs(s, 0, path, result, dp);

    return result;
  }

  void dfs(string& s, int start, vector<string>& path, vector<vector<string>>& result, vector<vector<bool>>& dp){
    int n = s.size();
    if(start == n){
      result.push_back(path);
      return;
    } 

    for(int i = start; i < n; i++){
      if(!dp[start][i]) continue;

      path.push_back(s.substr(start, i - start + 1));
      dfs(s, i + 1, path, result, dp);
      path.pop_back();
    } 
  }
};

