    //AC - 4ms - count the 1-bit;
    bool isPowerOfTwo(int n)
    {
        if(n < 1) return false;
        int count = 0;
        while(n)
        {
            if(n & 1) count++;
            n >>= 1;
        }
        return count>1? false : true;
    }



----------

> Bit manipulation -> always the coolest!


    //AC - 4ms - using n&n-1 to ensure there is only one 1-bit;
    bool isPowerOfTwo(int n)
    {
        return n>0 && !(n&(n-1));
    }


