public:                    
    bool isPowerOfTwo(int n) {
        int i=0;
        int store=0;                         
        if (n<=0){
            return false;
        }

        while(store!=n){
            store= pow(2,i);                    
            if (n%store!=0){
                break;
            }
            else {
                if (store==n){
                    return true;
                }
                i++;
            }
        }
        return false;
    }
};


