class Solution {
public:
    bool isValidSerialization(string preorder) {

        int count = 0;
        int n = preorder.length();
        for(int i=0;i<n;i++)
        {
            if(preorder[i]==',')continue;

            // count<0 happens when node has no parent EX => ["#,1,#"]
            if(count<0) return false;

            // decrease count as we find NULL
            if(preorder[i]=='#')count--;
            else
            {
                while(i<n && preorder[i]!=',')i++;

                // increase count as there is a node
                count++;
            }            
        }

        // Valid preorder would have N node and N+1 NULL nodes and count would be -1
        return (count==-1);
    }
};
```

