class Solution
{
    public:
        bool isValidSerialization(string p)
        {
            //creating Tokens
            vector<string> tokens;
            stringstream check(p);
            string temp;
            while (getline(check, temp, ','))
            {
                tokens.push_back(temp);
            }
            int v = 1;	//vacancy
            //checking for valid preorder
            for (auto s: tokens)
            {
                v--;
                if(v<0){
                    return false;
                }
                if (s != "#")
                {
                    v += 2;
                }
            }
            return v == 0;
        }
};
```

