public class Solution {
    int idx;
    public boolean isValidSerialization(String preorder) {
        String[] strs = preorder.split(",");
        if (strs.length == 0) return true;
        if (strs.length == 1) {
            if (strs[0].equals("#")) return true;
            else return false;
