        if (idx == strs.length) return true;
        if (strs[idx].equals("#")) {
            idx++;
            return true;
        }
        idx++;
        if (idx == strs.length) return false;
        boolean left = preorder(strs);
        if (idx == strs.length) return false;
        boolean right = preorder(strs);
        return left && right;
    }
}

