    bool f(string &s, int &idx) {
        if(idx >= s.size()) return false;
        if(s[idx] == '#') {
            idx = min(idx+2, (int)s.size());
            return true;
        }
        while(idx < s.size() && s[idx++] != ',');
        return f(s, idx) && f(s, idx);
    }
};
