class Solution {
public:
    bool isValidSerialization(string preorder) {
        if (preorder.empty()) return true;

        int expected_chars = 1;  // root
        int next_expected_chars = 0;

        stringstream ss(preorder);
        string item;
        while (getline(ss, item, ',')) {
            if (expected_chars == 0) return false;  // we did not expect any more chars
            if (item != "#") next_expected_chars += 2;  // any non-null node will require 2 children in the next level
            if (--expected_chars == 0) {  // end of level
                expected_chars = next_expected_chars;
                next_expected_chars = 0;
            }
        }

        // Were we expecting any more chars?
        return expected_chars == 0;
    }
};

