public class Solution {
    public bool IsValidSerialization(string preorder) {
        var t = String.Empty;
        var r = new Regex(@"[0-9]+,\#,\#", RegexOptions.Compiled);
        while (preorder != t)
        {
            t = preorder;
            preorder = r.Replace(preorder, "#");
        }

        return preorder == "#";
    }
}

