```
struct NODE{
    int val;
    int left = 0;
    int right = 0;
    NODE(int v){val = v;}
};
class Solution {
public:
    bool isValidSerialization(string preorder) {
        if(preorder == "#") return true;
        if(preorder[0] == '#') return false;
        /* Construction of nums array to store node values*/
        vector<int> nums;
        string node;
        for(int i=0; i<preorder.size(); i++){
            //encounter a comma
            if(preorder[i] == ','){
                //compute the node
                if(node == "#") {nums.push_back(-1); node = "";}
                else{
                    int number=0;
                    for(char &x: node){
                        number*=10;
                        number+=(int)(x-'0');
                    }
                    nums.push_back(number);
                    node = "";
                }
                //end of this block
            }
            else node.push_back(preorder[i]);
        }
        //compute the last node because we ddn't encounter comma at the end
        if(node == "#") nums.push_back(-1);
        else{
            int number=0;
            for(char &x: node){
                number*=10;
                number+=(x-'0');
            }
            nums.push_back(number);
        }
        //end of this block
        //for(int &x:nums) cout<<x<<" ";
        /*nums array constructed*/
        stack<NODE*> tree; //created a stack to push and pop nodes
        NODE* root = new NODE(nums[0]);
        tree.push(root); 
        //first pushed the root with left and right branches 0 indicating that left and right trees haven't completed
        for(int i=1; i<nums.size(); i++){
            //if both branches become 1...then we have to pop that node from stack
            //because the subtree has completed
            NODE* top = tree.top();
            while(top->left and top->right) {
                tree.pop();
                if(tree.empty()) return false; //because the stack cannot become empty before we complete our traversal
                top = tree.top();
                if(!top->left) top->left = 1;
                else top->right = 1;
            }            
            //if we encounter -1
            //this means that the child branch has ended
            //so we make branch of the top of stack as 1 which was previously 0
            if(nums[i] == -1){
                if(!top->left) {top->left = 1; continue;}
                else {top->right = 1; continue;}
            }

            //if we encounter a normal value
            //we'll push the node
            if(nums[i]>=0){
                NODE* newnode = new NODE(nums[i]);
                tree.push(newnode);
            }
        }
        NODE* top = tree.top();
        while(top->left and top->right) {
            tree.pop();
            if(tree.empty()) return true; //all the branches were traversed
            top = tree.top();
            if(!top->left) top->left = 1;
            else top->right = 1;
        }     
        return false; //we couldn't fill up the tree
    }
};
```

