class Solution {
public:
    bool isValidSerialization(string preorder) {
        int internalNodes = 0,leafNodes = 0, n = preorder.length();
        for(int i=0;i<n;i++){
            if(leafNodes>internalNodes)
                return false;
            if(preorder[i]=='#')leafNodes++;
            else
                internalNodes++;
            while(i<n && preorder[i]!=',')
                i++;
        }
        return (leafNodes==internalNodes+1);
    }
};
