class Solution {
    public boolean isValidSerialization(String preorder) {
        String[] nodes = preorder.split(",");

        int vacancy = 1;

        for(String node : nodes){
            // visiting node
            vacancy--;

            // checking -ve case
            if(vacancy<0) return false;

            // for non null node there is always a chance of two child
            if(!node.equals("#")){
                vacancy += 2;
            }
        }
        return vacancy == 0;
    }
}
