bool isValidSerialization(string pre) {
        if(pre[0]=='#'){  //if root node #
            if(pre.size()==1)return true;
            return false;
        }
        int null=0,notnull=0;  // counters for null and notnull nodes
        for(int i=0;i<pre.size();i++){
