public boolean isValidSerialization(String preorder) {
    String[] strs = preorder.split(",");
    Stack<String> stack = new Stack<>();
    for(int i = 0; i < strs.length; i++) {
        stack.push(strs[i]);
        if (stack.size() >= 3) {
            if(!helper(stack)) return false;
        }
    }
    return stack.size() == 1 && stack.peek().equals("#");
}

private boolean helper(Stack<String> stack) {
    String top = stack.pop();
    String second = stack.peek();
    stack.push(top);
    while (!stack.isEmpty() && stack.peek().equals("#") && second.equals("#")) {
        stack.pop(); // first #
        stack.pop(); // second #
        if(stack.peek().equals("#")) {
            return false; // "#, #, #"
        } else {
            stack.pop(); // pop a number
            if(stack.size() >= 2) {
                second = stack.peek();
                stack.push("#");
            } else {
                stack.push("#");
                break;
            }
        }
    }
    return true;
}

