    public class Solution {
        public boolean isValidSerialization(String preorder) {
            String[] seq = preorder.split(",");
            int stackSize = 0, p = 0;
            for (; p<seq.length; ++p) {
                if (seq[p].equals("#")) { 
                    if (stackSize == 0) { break; } 
                    --stackSize;
                }
                else { ++stackSize; }
            }
            return stackSize==0 && p==seq.length-1;
        }
    }

