class Solution {
public:
    int ind=0;
    bool isValidSerialization(string s) {
        if(ind>=s.length())
            return 0;

        int f=ind;
        string t="";
        while(ind<s.length() && s[ind]!=',')
            t+=s[ind++];
        ind++;
        if(t=="#") // null node ( can't have child )
        {
            if(f==0 && ind!=s.length()+1) // if root is NULL but has child
                return 0;
            return 1;
        }
        int p=isValidSerialization(s); // left child
        p=isValidSerialization(s); // right child

        if(p==0)// index out of bound ( not enough tree )
            return 0;
        if(f==0 && ind!=s.length()+1)// ( total tree is not covered yet)
            return 0;

        return 1;        
    }
};
