// DFS
var isValidSerialization = function(preorder) {
    let arr = preorder.split(',');
    var idx = 0;

    function verify() {
        if (idx >= arr.length) return false;
        if (arr[idx] === '#') {
            idx++;
            return true;
        }
        idx++;
        let left, right;
        left = verify();
        right = verify();
        return left && right;
    }

    let result = verify();
    if (idx <= arr.length - 1) return false;
    else return result;
};
