class Solution {
public:
    bool isValidSerialization(string preorder) {

      std::vector<int> tree = { 0 }; // counts how many child is completed

      preorder+=','; // final comma to handle last element
      char prev = 0; // prev character to deduce if it is null or not
      for(char c: preorder)
      {
        if(c!=',')
        {
          prev = c;
          continue;
        }

        if(tree.empty())
          return false;

        if(prev=='#') // nullptr
          tree.back()++;
        else // number
          tree.push_back(0);

        // normalize
        while(!tree.empty() && tree.back()==2)
        {
          tree.pop_back();
          if(!tree.empty())
            tree.back()++;
        }
      }

      return tree.size()==1 && tree.back()==1;
    }
};
```

