class Solution {
    public boolean isValidSerialization(String preorder) {
        StringBuilder sb = new StringBuilder();
        int count = 1;

        for(int i = 0; i < preorder.length(); ++i) {
            while(i < preorder.length() && preorder.charAt(i) != ',')
