    public:
    bool isValidSerialization(string preorder)
    {
        int stack=0;
        int i=0;
        while(i<preorder.size())
        {
            if(preorder[i]==',')//ignore commas
            {
                i++;
                continue;
            }
            else if(preorder[i]!='#')//number
            {
                while(i<preorder.size() && preorder[i]!=',')//scan whole number
                    i++;
                stack++;
                continue;
            }
            if(stack==0)
                break;
            i++;
            stack--;
        }
        return stack==0 && i==preorder.size()-1;
    }
};
