class Solution 
{
    public:
    bool next(string &s, int &i)
    {
        bool flag = true;
        if(s[i] == '#') flag = false;
        while(i != s.size()) 
            if( s[i++] == ',') break;
        return flag;
    }
    bool isValidSerialization(string preorder) 
    {
        int stack = 0, lim_id = preorder.size(), i = 0;
        while(i != lim_id)
            if(next(preorder, i)) stack++;
        else if(stack) stack--;
        else return i == lim_id;
        return false;
    }
};
