impl Solution {
    pub fn is_valid_serialization(preorder: String) -> bool {
        let mut iter = preorder.split(',').map(|s| s != "#");
        Self::dfs(&mut iter) && iter.next().is_none()
    }
    fn dfs(iter: &mut impl std::iter::Iterator<Item = bool>) -> bool {
        if let Some(b) = iter.next() {
            if b {
                if !Self::dfs(iter) {
                    return false;
                }
                if !Self::dfs(iter) {
                    return false;
                }
            }
            true
        } else {
            false
        }
    }
}
