class Solution {
public:
    // convert space seperated string to tokens
    vector<string> tokenizeString(string &s) {
        vector<string> res;
        stringstream ss(s);
        string word;
        while (ss >> word)
            res.push_back(word);
        return res;
    }
    // validate the preoder encoding
    bool isValidPreorder(vector<string> &pre, int &n) {
        // base cases
        if (n >= pre.size()) return false;
        if (pre[n] == "#") return true;
        // below reflect Preorder (DFS) technique
        bool left = isValidPreorder(pre, ++n);
        bool right = isValidPreorder(pre, ++n);
        // root is valid only if left and right are valid
        return left and right;
    }
    // main
    bool isValidSerialization(string preorder) {
        // replace ',' by ' ' in preoder string
        replace_if(preorder.begin(), preorder.end(), [] (char &c) {return c == ',';}, ' ');
        // tokenize preoder into pre vector
        vector<string> pre = tokenizeString(preorder);
        // check if preOrder is valid or not
        int n = 0;
        // n == pre.size() - 1 is checked to make sure the whole string was processed,
        // if not => given encoding is invalid prorder
        return isValidPreorder(pre, n) and n == pre.size() - 1;
    }
};
```

