class Solution {
public:
    bool isValidSerialization(string preorder) {
        int nodes = 1 , n =preorder.length(); 
        for(int i=0;i<n;i++){
            if(preorder[i]==',')
                continue; 
            --nodes;
            if(nodes<0)
                return 0;            
            if(preorder[i]!='#'){  //each non null node can have a left and right child 
