class Solution {
public:
    bool isValidSerialization(string preorder) {
        if(preorder=="")
            return false;
        preorder+=',';
        vector<string>result;
        string temp;
        for(char c:preorder){
            if(c==','){
                result.push_back(temp);
                temp="";
                continue;
            }
            temp+=c;
        }

        //result just has # and int values now
        stack<string>st;
        for(int i=0;i<result.size();i++){
            string curr = result[i];
            while(curr=="#" && !st.empty() && st.top()==curr){
                st.pop();   //pop out one 
                if (st.empty()) {   //if stack becomes empty before string ends - root is processed
                    return false;
                }
                st.pop();   //done processing this child
            }
            st.push(curr);   //push the curr char
        }
        return st.size() == 1 && st.top()=="#";
    }
};
```
