class Solution {
public:
    bool isValidSerialization(string preorder) {
        if (preorder.empty()) return false;
        preorder+=',';

        int sizeOfTree = 1;

        for(int i=0;i<preorder.size();i++){
            if(preorder[i]!=',')continue;   //process only at commas(ending of node val)

            sizeOfTree--;                   //used one available capacity
            if (sizeOfTree<0) return false; //cant accomodate more nodes

            if(preorder[i-1]!='#')  //if prev node was non-null - we can add 2 more nodes - right and left
                sizeOfTree+=2;
        }
        return sizeOfTree==0;
    }
};
