var isValidSerialization = function(preorder) {
  if (!preorder) return false;

  const arr = preorder.split(',');
  let idx = 0;

  const traverse = () => {
    const val = arr[idx++];

    if (!val) return false;
    if (val === '#') return true;

    let left = traverse();
    let right = traverse();

    return left && right;
  };

  return traverse() && !arr[idx];
};
