class Solution {
public:
    bool isValidSerialization(string preorder) {
        stack<char> s;
        int i = preorder.size() - 1;
        for (; i >= 0; i --) {
            if (preorder[i] == ',') continue;
            if (preorder[i] == '#') s.push(preorder[i]);
            else {
                if (s.size() < 2) {
                    s.push('a'); s.push('a');
                    break;
                }
                s.pop(); s.pop(); s.push(preorder[i]);
                while(i >= 0 && preorder[i] != ',') -- i;
            }
        }
        //cout << s.size() << "\n";
        return s.size() == 1;
    }
};
