class Solution {
public:
    bool isValidSerialization(string preorder) {
        stack<string> st;

        stringstream ss(preorder);
        string curr;


        while(getline(ss, curr, ','))
        {
            st.push(curr);
            while(st.size() >= 3)
            {
                string one,two,three;
                one = st.top();
                st.pop();

                two = st.top();
                st.pop();

                three = st.top();
                st.pop();

                // cout<<three<<":"<<two<<":"<<one<<endl;

                if(one == two && two=="#" && three != "#")
                {
                    st.push("#");
                    continue;
                }


                st.push(three);
                st.push(two);
                st.push(one);
                break;
            }
        }
        if(st.size()==1 && st.top()=="#")
            return true;

        return false;
    }
};
