class Solution {
    public int findPairs(int[] nums, int k) {
        int kDiffs = 0;
        Map<Integer, Integer> counter = new HashMap<>();

        for (int n: nums) {
            int countOfN = counter.getOrDefault(n, 0);
            countOfN++;
            counter.put(n, countOfN);

            if (k == 0) {
                if (countOfN == 2) kDiffs++;
            } else if (countOfN == 1) {
                if (counter.containsKey(n - k)) kDiffs++;
                if (counter.containsKey(n + k)) kDiffs++;           
            }
        }

        return kDiffs;
    }
}

