int findPairs(vector<int>& nums, int k) {;
    unordered_map<int, int> freq;
    int ans = 0;
    for (int n : nums) {
        if (k == 0) {
            if (freq[n] == 1) ++ans; // only count once
        } else if (!freq.count(n)) { // only count when the first time n appears
            if (freq.count(n - k)) ++ans;
            if (freq.count(n + k)) ++ans;
        }
        ++freq[n];
    }
    return ans;
}
