class Solution 
{
    public int findPairs(int[] nums, int k) 
    {
        int n = nums.length;
        int count =0;
        Arrays.sort(nums);
        if(k==0)
        {
           int pre = 100000001;
           for(int i=0;i<(n-1);i++)
           {
               if((nums[i]==nums[i+1]) && pre!=nums[i])
               {
                   count++;
               }
               pre = nums[i];
           }
           return count;
        }
        int pre = 100000001;
        for(int i=0;i<n;i++)
        {
            if(pre!=nums[i])
            {
               count = count + search(nums,i,k,n-1);
               pre = nums[i]; 
            }
        }  
        return count;
    }
    public int search(int[] nums,int l,int k,int r)
    {
        int target = nums[l];
        int t = (target+k);
        while(l<=r)
        {
            int mid = (l+r)/2;

            if(nums[mid]==t)
            {
                return 1;
            }
            else if(nums[mid]>t)
            {
                r = mid-1;
            }
            else
            {
                l = mid+1;
            }

        }
        return 0;
    }
}
```

