            for (int j=i+1; j<n; j++) 
            {
                if (nums[j] - firstNumber >  k) {
                    break;
                }

                if (nums[j] - firstNumber == k)
                {
                    if (!result.Contains(firstNumber) || !result.Contains(nums[j])) {
                        count++;
                    }
                    result.Add(firstNumber);
                    result.Add(nums[j]);                    
                }
            }
        }
        return count;
    }
